    jQuery.validator.addMethod("phone", function(value, element) {  
    var regName = new RegExp(/^1[3|4|5|8][0-9]\d{4,8}$/);
    return this.optional(element) || regName.test( value );    
    }, "请输入正确的手机号");  
    jQuery.validator.addMethod("passport", function(value, element) {  
    var regName = new RegExp(/^[a-zA-Z0-9]{3,21}$/);
    var regName2 = new RegExp(/^(P\d{7})|(G\d{8})$/);
    return this.optional(element) || regName.test( value ) || regName2.test( value );
    }, "请输入正确的护照号");  
     jQuery.validator.addMethod("mydate", function(value, element) {  
    var regName = new RegExp(/^\d{2}\/\d{2}\/\d{4}$/);
    return this.optional(element) || regName.test( value );
    }, "请输入有效的日期 (MM/DD/YYYY)");  
    jQuery.validator.addMethod("myarea", function(value, element) {  

    return this.optional(element) || value != 0;
    }, "请选择所属区域");  
    jQuery.validator.addMethod("notling", function(value, element) {  

    return this.optional(element) || value != 0;
    }, "您的选择有误！");  
    jQuery.validator.addMethod("notre", function(value, element) {  
    $(element).parent().parent().parent().children('#new_pass').children('div').children('input').val();
    return this.optional(element) || $(element).parent().parent().parent().children('#new_pass').children('div').children('input').val() == value;
    }, "两次密码不一致"); 
    jQuery.validator.addMethod("notnew", function(value, element) {  
    var s= $(element).parent().parent().parent().children('#re_pass').children('div').children('input').val();
    return this.optional(element) || s == value || s =="";
    }, "两次密码不一致");   
    jQuery.validator.addMethod("beizhu", function(value, element) {  
       var type_id=$(element).parent().parent().parent().parent().children('#leixing').val();
       return $("#n_"+type_id).val() !='';
      
    }, "输入不合法");   
   